import React from "react"
import PropTypes from "prop-types"

const HederMenu = (props) => {
  // debugger
  return (
    <div>
<div>
      {/* Header content */}
      <div className="row header_around">
        {/* ... Other header content ... */}
        <div className="col-sm-4 location custom-dropdown">
          {/* Location dropdown */}
          <select
            value={selectedLocation}
            onChange={(e) => setSelectedLocation(e.target.value)}
          >
            {/* Map over locations to generate options */}
            {locations.map((location) => (
              <option key={location} value={location}>
                {location}
              </option>
            ))}
          </select>
        </div>
        {/* ... Other header content ... */}
      </div>

      {/* ... Other content ... */}

      {/* Conditionally render user-related content */}
      {userSignedIn ? (
        <>
          {/* User is signed in */}
          <span className="sign_in">
            <Link to={`/profile/${currentUserId}`}>{currentUserEmail}</Link>
          </span>
          <button onClick={handleLogout}>Log out</button>
        </>
      ) : (
        <>
          {/* User is not signed in */}
          <span className="sign_in">
            <Link to="/sign-in">Sign in</Link>
          </span>
        </>
      )}
    </div>
  );
};    </div>


  )
}


export default HederMenu
