// import React from "react"
import PropTypes from "prop-types"
import React, { useRef } from 'react';

import image from "../../../assets/images/food.jpg"
import "../../../assets/stylesheets/home/restaurants.css"
// import '/home/chaithanya/chaithanya/swiggy/app/assets/stylesheets/header.css'

const Restaurants = (props) => {
  // debugger
  // const scrollContainerRef = 50;
  const scrollContainerRef = useRef(null);

  const handleScroll = (direction) => {
    const scrollDistance = 50; // Adjust the scroll distance as needed
    const scrollContainer = scrollContainerRef.current;

    if (scrollContainer) {
      if (direction === 'left') {
        scrollContainer.scrollLeft -= scrollDistance;
      } else if (direction === 'right') {
        scrollContainer.scrollLeft += scrollDistance;
      }
    }
  };

  return (
      <div className="  body_center">
        <h4>What's on your mind?</h4>
        <button onClick={() => handleScroll('left')}>Scroll Left</button>
         <button onClick={() => handleScroll('right')}>Scroll Right</button>

        <div className=" horizontal-scroll " ref={scrollContainerRef} >
          {props.categories.map((category, index) => (
          <div className="scroll-content" key={index}>
            <div className="categorie_image">
            <a href="/">
              <img className="logo_image" src={image} alt=" logo" height={75} width={75}/>
            </a>
            <div>
              {category.name}
              </div>
          </div>
          </div>
          ))}
        </div>        
      </div>
  )
}


export default Restaurants
