import React from "react"
import PropTypes from "prop-types"
import image from "../../../assets/images/paneer.avif"

import "../../../assets/stylesheets/restaurants/food_items.css"

const FoodItems = (props) => {
  const result = 0

  const checkIfItemInCart = (itemId) => {
    const matchingItem = props.item_in_cart.find(item => item.food_item_id === itemId);

    if (matchingItem) {
      return {
        isInCart: true,
        quantity: matchingItem.quantity,
      };
    } else {
      return {
        isInCart: false,
        quantity: 0,
      };
    }
  };

  return (
    <React.Fragment>
      <div className="main_body">
        <div>
          <h5>{props.restaurant.name}</h5>
          <small>{props.restaurant.spaciality}</small>
          <br/>
          <small>{props.restaurant.area}</small>
          <hr/>
        </div>
        <div>
          <div className="row"></div>
          {props.food_items.map((item, index)=>(
            <div className="col-12">
              <div  >
                <div className="row">
                  <div key={index} className="col-md-10">
                    <h4 >{item.name}</h4>
                    <p>Rs. {item.price}</p>
                    <p >{item.description}</p>
                  </div>
                  <div className="col-md-2 image-container">
                    <div className="">
                      <img className="food_item_image image " src={image} alt=" food_item_image" height={96} width={118}/>
                       <div className="cart-container">
                        <div className="cart-container-items"  result= {checkIfItemInCart(item.id)}>

                          { result.isInCart ?
                          ( <div className="cart-button"> ADD </div>
                          ):(
                            <div>
                              <div className="row">
                                <div className="col-4">-</div>
                                <div className="col-4">{result.quantity}</div>
                                <div className="col-4">+</div>

                              </div>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>


                  </div>
                </div>
              </div>
              <hr/>
            </div>

          ))}
        </div>


      </div>
    </React.Fragment>
  )
}


export default FoodItems
